function rot13(str) { // LBH QVQ VG!
  var alph= "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
  alph = alph.split('');

str = str.split('');
//Loop through the array
for (var i = 0; i < str.length; i++){
//if it is above 12, take away 13 and replace with the letter
if (alph.indexOf(str[i]) >= 13){
    str[i] = alph[alph.indexOf(str[i])-13];
}  
//If it is below thirteen, but not -1 (for other characters), add thirteen and replace with the letter
else if(alph.indexOf(str[i]) < 13 && alph.indexOf(str[i]) != -1){
    str[i] = alph[alph.indexOf(str[i])+13];
}
}
//console.log(str.join(''));
return str.join('');
}



