function smallestCommons(arr) {

//Function to check if a goes into b.
  function isDiv(a, b){
    if(Math.floor(b/a) == b/a){
      return true;
    } else {
      return false;
    }

  }

//Function to but two element array in ascending order  
  function orderArray(arr){
    if (arr[0] > arr[1]){
      var b = arr[0];
      arr[0] = arr[1];
      arr[1] = b;
    }
    return arr;
  } 

//function to find lowest common multiple of a & b
  function lcm(a, b){ 
    for (var i = 1; i <= a*b; i++){
      if (isDiv(a, i) && isDiv(b, i)){
        return i;
      }
    }
    return a*b;
  }

//Put array in ascending order (if it isn't already)
orderArray(arr);

//set an upper bound, so the for loop definitely terminates
var maxValue = 1;
for (var i = arr[0]; i <= arr[1]; i++){
    maxValue = maxValue * i;
  }

